hudson.tasks.Shell.command:
#!/usr/bin/env bash export DOCKER_ENV=int export COMPOSE_PROJECT_NAME=projectname_${DOCKER_ENV:--local} export COMPOSE_FILE=build.devcloud.yml
# Break down the Docker environment and remove generated files. teardown() { docker-compose -f docker-compose.yml stop || true docker-compose -f docker-compose.yml rm -f || true # Now that www container is stopped it's safe to clean up. # dev-support does this for the cleanest possible checkup. # git clean -fd }
# Handler for errors or other interruptions. cancel() { echo "$JOB_NAME: Error: Line $1: $2" # Since CI runs teardown on exit it is not needed here. # teardown exit 34 }
# End-of-job cleanup. complete() { ret=$1 [ "$ret" -eq 0 ] || echo >&2 "$JOB_NAME: aborted ($ret)" teardown }
# Abort build step on errors. trap 'cancel $LINENO $BASH_COMMAND' ERR SIGINT SIGTERM SIGQUIT SIGHUP # Clean up on program termination. trap 'complete $?' EXIT
teardown
# Set up and install site docker-compose -f docker-compose.yml up -d docker-compose run --rm cli "NPM_CONFIG_LOGLEVEL=error npm install" docker-compose run --rm grunt --timer
# Remove --no-db-load if each deploy should re-load a database from /opt/backups/latest.sql.gz. # The location and name of this file can be changed in Gruntconfig.json. docker-compose run --rm grunt install --no-db-load
# Run cron for maximized database freshness. docker-compose run --rm drush cron
# Export database. This process preserves the latest 5 databases. DATE=$(date +%Y-%m-%d_%H:%M:%S) docker-compose run --rm cli bin/db-export.sh nightlies/$DATE.sql
# Run Behat Tests # Highly recommended, but not always used on projects. # docker-compose run --rm cli sh -c "GDT_DOMAIN=${DOCKER_ENV}-projectname.hosting.example.net grunt test --force --timer"
# Perform a scan for module updates. docker-compose run --rm cli bash bin/update-scanner.sh
