hudson.tasks.Shell.command:
#!/usr/bin/env bash
export DOCKER_ENV=review
export COMPOSE_PROJECT_NAME=projectname_${DOCKER_ENV:--local}
export COMPOSE_FILE=build.devcloud.yml
export SITE_DOMAIN=${DOCKER_ENV}-projectname.hosting.example.net

# Break down the Docker environment
teardown() {
  docker-compose -f docker-compose.yml stop
  docker-compose -f docker-compose.yml rm -f
}

# Handler for errors or other interruptions.
cancel() {
  echo "$JOB_NAME: Error: Line $1: $2"
  exit 32
}

# End-of-job cleanup.
complete() {
  ret=$1
  [ "$ret" -eq 0 ] || echo >&2 "$JOB_NAME: aborted ($ret)"
}

# Abort build step on errors.
trap 'cancel $LINENO $BASH_COMMAND' ERR SIGINT SIGTERM SIGQUIT SIGHUP
# Clean up on program termination.
trap 'complete $?' EXIT

teardown
